import React, { Component } from 'react';
import Product from './components/Product';
import './App.css';

class App extends Component {
// Cách 1 lấy giá trị value thông qua refs
  //   constructor(props) {
//     super(props);
//     this.onAddProduct = this.onAddProduct.bind(this);
// }
  onAddProduct = () =>{
    console.log(this.refs.name.value);
  }
  render() {
    var products = [
      {
        id : 1,
        name  : "Apple Iphone",
        price : "15000000",
        image : "https://cdn.tgdd.vn/Products/Images/42/114115/iphone-x-64gb-1-400x460.png",
        status : true
      },
      {
        id : 2,
        name  : "Samsung",
        price : "15000000",
        image : "https://cdn.tgdd.vn/Products/Images/42/114115/iphone-x-64gb-1-400x460.png",
        status: true 
      },
      {
        id : 3,
        name  : "Ipad Iphone",
        price : "12000000",
        image : "https://cdn.tgdd.vn/Products/Images/42/114115/iphone-x-64gb-1-400x460.png",
        status: true
      }
    ];

    let elements = products.map((product, index) => {
         let result = ''
         if(product.status === true ){
        return <Product 
            key = {product.id}
            price = {product.price}
            image = {product.image} 
        >
          {product.name}
        </Product>
         }
         return result;
    });

    return (
      <div>
          <nav className="navbar navbar-default">
  <div className="container-fluid">
    <div className="navbar-header">
      <a className="navbar-brand" href="cuong.html">Props</a>
    </div>
  </div>
</nav>
<div className="container">
   <div className="row">
       <div className="col-xs-12">
          {elements}
       </div>
       <div className="col-xs-12">
        <div className="panel panel-danger">
           <div className="panel-title">Thêm sản phẩm </div>
        </div>  
        <div className="panel-body">
        <div className="form-group">
            <label>Tên sản phẩm </label>
            <input type="text" className="form-control" ref="name" />
         </div>
         <button onClick= { this.onAddProduct }  className="btn btn-primary" type="submit"> Lưu</button>
        </div>
       </div>
   </div>
</div>      
    </div>
    );
  }
}

export default App;
